func:
let distance = global.get('distance_measurement');

if (distance == "miles")
{
    msg.payload.distance = msg.payload.distance_miles;
}

if (distance == "km")
{
    msg.payload.distance = msg.payload.distance_km;
}

if (msg.payload.distance == null)
{
    msg.payload.distance = 0;
}

return msg;

