//    return {"payload":{totalnoqsos:0}};
//}

let arraylength = msg.payload.dynamicresults.breakdown[0].qso.length
totalnoqsos = 0;

for (let i=0; i < msg.payload.dynamicresults.breakdown[0].qso.length; i++)
{

    if (msg.payload.dynamicresults.breakdown[0].qso[i].$.band == "total" && msg.payload.dynamicresults.breakdown[0].qso[i].$.mode == "ALL")
    {
        totalnoqsos = parseInt(msg.payload.dynamicresults.breakdown[0].qso[i]._);
        flow.set('totalnoqsos',totalnoqsos)
        return {"payload":{totalnoqsos:totalnoqsos}};

    }

}


return;

if (msg.payload.dynamicresults.score == "0")
{
    flow.set('totalpoints',0)
    return {"payload":{totalpoints:0}};
}

let arraylength = msg.payload.dynamicresults.breakdown[0].point.length
let totalpoints = 0;

for (let i=0; i < msg.payload.dynamicresults.breakdown[0].point.length; i++)
{

    if (msg.payload.dynamicresults.breakdown[0].point[i].$.band == "total" && msg.payload.dynamicresults.breakdown[0].point[i].$.mode == "ALL")
    {
        totalpoints = parseInt(msg.payload.dynamicresults.breakdown[0].point[i]._);
        flow.set('totalpoints',totalpoints);
        return {"payload":{totalpoints:totalpoints}};

    }

}


return;

if (msg.payload.dynamicresults.breakdown[0].mult == undefined)
{
    flow.set('totalmults',null)
    return {"payload":{totalmults:0}};
}

let arraylength = msg.payload.dynamicresults.breakdown[0].mult.length
let totalmults = 0;

for (let i=0; i < msg.payload.dynamicresults.breakdown[0].mult.length; i++)
{

    if (msg.payload.dynamicresults.breakdown[0].mult[i].$.band == "total" && msg.payload.dynamicresults.breakdown[0].mult[i].$.mode == "ALL")
    {
        totalmults = parseInt(msg.payload.dynamicresults.breakdown[0].mult[i]._)
//        totalmults = totalmults + total;
        break;
    }

}
flow.set('totalmults',totalmults);
return {"payload":{totalmults:totalmults}};

//if (msg.payload.score == 0)
//{
//    return {"payload":{totalmult:0}};
//}

let arraylength = msg.payload.dynamicresults.breakdown[0].mult.length
let totalmult = 0;
//return {"payload":{arraylength:arraylength}};


if (arraylength == 0)
{
	totalmult = msg.payload.dynamicresults.breakdown[0].mult[0]._
	return {"payload":{totalmult:totalmult}}
}

totalmult = msg.payload.dynamicresults.breakdown[0].mult[arraylength-2]._;

return {"payload":{totalmult:totalmult}};

