func:
let x = msg.payload.RadioInfo.TXFreq[0];
let len = x.length;


if (len == 7)
{
    let runfreq = msg.payload.RadioInfo.TXFreq[0];

    let first = runfreq.substr(0,5);
    let last = runfreq.substr(5,1);
    let freq = first+'.'+last;

let spot = {payload: freq};
return spot; 

}
if (len == 6)
{
    let runfreq = msg.payload.RadioInfo.TXFreq[0];

    let first = runfreq.substr(0,4);
    let last = runfreq.substr(4,1);
    let freq = first+'.'+last;

let spot = {payload: freq};
return spot; 

}
else
{
return msg;
}

let x = msg.payload.RadioInfo.IsRunning[0];

if (x == "True")
{
    let status = "Run";

let msg = {payload: status};
return msg; 

}
if (x == "False")
{
    let status = "S&P";

let msg = {payload: status};
return msg; 

}
else
{
    let status = "Unknown";

let msg = {payload: status};
return msg;
}

if (msg.payload.RadioInfo.IsRunning[0] == "True")
{
    msg.color = "#008000"
}

if (msg.payload.RadioInfo.IsRunning[0] == "False")
{
    msg.color = "#FF0000"
}

return msg;

let isTX = msg.payload.RadioInfo.IsTransmitting[0]

if (isTX == "True")
{
    msg.payload = "True";
    return msg;
}

if (isTX == "False")
{
    msg.payload = "False";
    return msg;
}

let isfocus = msg.payload.RadioInfo.FocusRadioNr[0]

if (isfocus == "1")
{
    msg.payload = "False";
    return msg;
}

if (isfocus == "2")
{
    msg.payload = "True";
    return msg;
}

// Determine what type of map label to display beside the map pin

let maplabels = global.get('maplabels')
let maplabeltype = global.get('maplabeltype')
let contesttype = global.get('contesttype')
let greatcircle = global.get('greatcirclelines')

delete msg.payload.ID

if (maplabels == true)
{
    if (maplabeltype == "bandmode" || maplabeltype == null)
    {
        msg.payload.label = msg.payload.band + ' ' + msg.payload.mode;
    }
    if (maplabeltype == "bandonly")
    {
        msg.payload.label = msg.payload.band;
    }
    if (maplabeltype == "modeonly")
    {
        msg.payload.label = msg.payload.mode;
    }
    if (maplabeltype == "callonly")
    {
        msg.payload.label = msg.payload.name
    }

}

// Add mults together.  Later in the flow if ismultiplier >= 1 then the contact is a mult.

let multi1 = msg.original.ismultiplier1[0];
let multi2 = msg.original.ismultiplier2[0];
let multi3 = msg.original.ismultiplier3[0];
msg.payload.ttl = msg.payload.ttl.toString();

var m1 = parseFloat(multi1);
var m2 = parseFloat(multi2);
var m3 = parseFloat(multi3);

msg.payload.ismultiplier = m1 + m2 + m3;

// Determine what type of Exchange to dispaly
// stateonly, countystate, namestate, sections, grids, zones, power, other or null


if (contesttype == "stateonly" || contesttype == "" || contesttype == "other")
{
    if (msg.payload.exchange == "")
    {
        msg.payload.exchange = msg.original.section[0];
    }
}
if (contesttype == "countystate")
{
    msg.payload.exchange = msg.original.exchange1[0];
}
if (contesttype == "namestate")
{
    msg.payload.exchange = msg.original.name[0] + " " + msg.original.section[0];
}
if (contesttype == "sections")
{
    msg.payload.exchange = msg.original.section[0];
}
if (contesttype == "classsection" || contesttype == "all")
{
    msg.payload.exchange = msg.original.exchange1[0] + " " + msg.original.section[0]
}
if (contesttype == "grids")
{
    msg.payload.exchange = msg.original.gridsquare[0];
}
if (contesttype == "zones")
{
    msg.payload.exchange = msg.original.zone[0];
}
if (contesttype == "serial")
{
    msg.payload.exchange = msg.original.rcvnr[0];
}
if (contesttype == "power")
{
    msg.payload.exchange = msg.original.power[0];
}
if (contesttype == "blank")
{
    msg.payload.exchange = "----";
}

if (msg.payload.exchange == "")
{
    msg.payload.exchange = msg.original.exchange1[0] + " " + msg.original.section[0];
}


// Derminte the location of the great circle lines

let lat1 = msg.payload.lat
let lon1 = msg.payload.lon
let lat = parseFloat(lat1);
let lon = parseFloat(lon1);
let homelat = global.get('homelat');
let homelon = global.get('homelon');

if (greatcircle == true)
{
    msg.payload.greatcircle = [[homelat,homelon], [lat,lon]]

    if (msg.payload.greatcircle[0][0] == undefined)
