// global variables separated by semicolon to be saved
var globallist = "PC1;PC2;PC3;PC4;PC5;PC6;Q1hrhigh;Q1hrlow;Q2hrhigh;Q2hrlow;contestcallsign;contesttype;dblookup;hamqthusername;hamqthpassword;qrzusername;qrzpassword;homelat;homelon;maplabels;maplabeltype;opsorttype;saymulti;skimmercall;ttl;distance_measurement;category;class;OS_Type;livedata";
var mylist = globallist.split(";");

var outputs = [];

for (i=0; i<mylist.length; i++) {
    outputs.push({ key : mylist[i], type: typeof global.get(mylist[i]), value: global.get(mylist[i])});
}


msg.payload = outputs;
return msg;

//msg.payload=typeof global.get("torrent_keywords");

var output = [];

for (var i=0; i<msg.payload.length; i++) {
    switch (msg.payload[i].type) {
        case 'undefined': 
            // the global variable probably had no value, nothing needs to be restored
            output.push(msg.payload[i].key + " is undefined.");
            break;
        case 'number':
            if (msg.payload[i].value==="NaN") {
                // there is no valid value to be restored, skip this variable
                output.push(msg.payload[i].key + " is NaN.");
            } else {
                if (msg.payload[i].value.toString().indexOf(".")>-1) {
                    // the value appears to be a float
                    global.set(msg.payload[i].key,parseFloat(msg.payload[i].value));
                    output.push(msg.payload[i].key + " is restored to " + msg.payload[i].value);
                } else {
                    global.set(msg.payload[i].key,parseInt(msg.payload[i].value));
                    output.push(msg.payload[i].key + " is restored to " + msg.payload[i].value);
                }
            }
            break;
        case 'string':
            global.set(msg.payload[i].key,msg.payload[i].value);
            output.push(msg.payload[i].key + " is restored to " + msg.payload[i].value);
            break;
        case 'boolean':
            global.set(msg.payload[i].key,msg.payload[i].value==="true");
            output.push(msg.payload[i].key + " is restored to " + msg.payload[i].value);
            break;
    }
}

msg.payload = output;
return msg;

