function decode(b) {
 var a, e = {},
 d = b.split(""),
 c = d[0],
 f = c,
 g = [c],
 h = 256;
 o = h;
 for (b = 1; b < d.length; b++) a = d[b].charCodeAt(0), a = h > a ? d[b] : e[a] ? e[a] : f + c, g.push(a), c = a.charAt(0), e[o] = f + c, o++, f = a;
 return g.join("")
}

return {
 payload: decode(msg.payload)
}

